package com.bridgelabz.hotelreservationsystem;

public class Hotel {
    private String nameOfHotel;
	private double weekdayRates;
    private double weekendRates;
    private double weekdayRewardRates;
    private double weekendRewardRates;
    private int rating;

	public Hotel(String name, double weekdayRates, double weekendRates,double weekdayRewardRates,double weekendRewardRates, int rating) {
		this.nameOfHotel=name;
		this.weekdayRates=weekdayRates;
		this.weekendRates=weekendRates;
		this.weekdayRewardRates=weekdayRewardRates;
		this.weekendRewardRates=weekendRewardRates;
		this.rating=rating;
	}
    

	public String getNameOfHotel() {
		return nameOfHotel;
	}
    
	public void setNameOfHotel(String nameOfHotel) {
		this.nameOfHotel = nameOfHotel;
	}
	
    public double getWeekdayRates() {
		return weekdayRates;
	}

	public void setWeekdayRates(double weekdayRates) {
		this.weekdayRates = weekdayRates;
	}

	public double getWeekendRates() {
		return weekendRates;
	}

	public void setWeekendRates(double weekendRates) {
		this.weekendRates = weekendRates;
	}
	
	public double getWeekdayRewardRates() {
		return weekdayRewardRates;
	}

	public void setWeekdayRewardRates(double weekdayRewardRates) {
		this.weekdayRewardRates = weekdayRewardRates;
	}

	public double getWeekendRewardRates() {
		return weekendRewardRates;
	}

	public void setWeekendRewardRates(double weekendRewardRates) {
		this.weekendRewardRates = weekendRewardRates;
	}
	
	public int getRating() {
		return rating;
	}

	public void setRating(int rating) {
		this.rating = rating;
	}
	
    
}
